mapper.select:
select id,type, order_num, supplier_id, creat_time, update_time, is_delete
from mw_warehouse_in
where id = #{id,jdbcType=INTEGER}

select inh.*,ios.*,io.*,st.*,inh.order_num,inh.type,p.name pname,p.code pcode,p.standards,ios.batch iosbatch,ios.change_num change_num,st.name stname,inh.type,ms.name mname,e.name ename,io.status status,em.name aname,io.audit_time from mw_warehouse_in inh left JOIN mw_warehouse_in_out_sub ios on ios.in_out_id = inh.id LEFT JOIN mw_warehouse_in_out io on io.change_id = inh.order_num left join mw_product p on p.id = ios.product_id LEFT JOIN mw_units u on u.id = p.store_unit_id left join mw_product_stock ps on ps.product_id = p.id left JOIN mw_supplier ms on ms.id = inh.supplier_id left JOIN mw_storage st on st.id = ios.storage_id left JOIN mw_employee e on io.form_emp_id = e.id left JOIN mw_employee em on io.audit_emp_id = em.id
and inh.is_delete = 0
and inh.order_num = #{orderNum}
and inh.type = #{type}
and io.status = #{status}
